mapper.select:
select * from notice
where notice_id = #{noticeId}
order by create_time desc

select *
from notice
where status = #{status1}
   or status = #{status2}
order by status desc,create_time desc

mapper.update:
update notice
set title   = #{title},
    keyword = #{keyword},
    content = #{content},
    status  = #{status}
where notice_id = #{noticeId}

update notice
set status = #{status}
where notice_id = #{noticeId}
